import React, {Component} from 'react';
import callApi from './../../utils/apiCaller';
import {Link} from 'react-router-dom';
class ProductActionPage extends Component {

    constructor(props){
        super(props);
        this.state={
            txtName: '',
            txtPrice: '',
            chkbStatus: '',
            id: ''
        }
    }

    componentDidMount(){
        var {match} =this.props;
        if(match){
            var id = match.params.id;
            callApi(`products/${id}`,'GET',null).then(res =>{
                var data = res.data;
                this.setState({
                    id: data.id,
                    txtName: data.name,
                    txtPrice:data.price,
                    chkbStatus: data.status
                })
            });
        }
    }

    onChange = (event) =>{
        var target = event.target;
        var name = target.name;
        var value = target.type === 'checkbox' ? target.checked : target.value;
        this.setState({
            [name]:value
        })
    }

    onSave = (event) => {
        event.preventDefault();
        var {history} = this.props;
        var {txtName,txtPrice,chkbStatus,id} = this.state;
        if(id){ //update
            callApi(`products/${id}`, 'PUT', {
                name: txtName,
                price: txtPrice,
                status: chkbStatus
            }).then(res =>{
                history.goBack();
            });
        }
        else{ //create
        callApi('products', 'POST', {
            name: txtName,
            price: txtPrice,
            status: chkbStatus
        }).then(res =>{
            history.goBack();
        });
    }
    }

    render(){
        var {txtName,txtPrice,chkbStatus} = this.state;
    return (
        <div className="col-xs-4 col-sm-4 col-md-4 col-lg-4">       
            <form onSubmit = {this.onSave}>
                <div className="form-group">
                    <label>Tên Sản Phẩm: </label>
                    <input type="text" className="form-control" name="txtName" value={txtName} onChange = {this.onChange}/>
                </div>
                <div className="form-group">
                    <label>Giá: </label>
                    <input type="number" className="form-control" name="txtPrice" value={txtPrice} onChange = {this.onChange}/>
                </div>
                <div className="form-group">
                    <label>Trạng Thái: </label>
                </div>
                <div className="checkbox">
                    <label>
                        <input type="checkbox" name="chkbStatus" checked={chkbStatus} value={chkbStatus} onChange = {this.onChange} />
                        Còn Hàng
                    </label>
                </div>
                <button type="submit" className="btn btn-primary mr-10">Lưu Lại</button>
                <Link to="/product-list" className="btn btn-danger ">Trở lại</Link>
            </form>
            
        </div>
    )
    }
}
export default ProductActionPage;
