import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import { connect } from 'react-redux';
import {actGetProductRequest} from '../../actions/index';
class ProductActionPage extends Component {

    constructor(props){
        super(props);
        this.state={
            txtName: '',
            txtPrice: '',
            chkbStatus: '',
            id: '',
            txtNumber: '',
            txtImg: ''
        }
    }
    componentDidMount(){
        var {match} =this.props;
        if(match){
            var id = match.params.id;
            this.props.onEditProduct(id);

        }
    }
    componentWillReceiveProps(nextProps){
        if(nextProps && nextProps.itemEditing){
            var {itemEditing} = nextProps;
            this.setState({
                id: itemEditing.id,
                txtName: itemEditing.name,
                txtPrice: itemEditing.price,
                chkbStatus: itemEditing.status,
                txtNumber: itemEditing.number,
                txtImg: itemEditing.img
            })
        }
    }
    render(){
        var {txtName,txtPrice,chkbStatus,txtNumber,txtImg} = this.state;
    return (
        <div className="container">       
            <div class="row">
                <div class="col-xs-6 col-sm-6 col-md-6 col-lg-6">
                    <img src={txtImg} class="img-responsive" alt="Image"/>>  
                </div>
                <div class="col-xs-6 col-sm-6 col-md-6 col-lg-6">
                    <h1>{txtName}</h1>
                    <h2>Gi√°: {txtPrice} VND</h2>
                </div>
                
            </div>
            
        </div>
    )
    }
}

const mapStateToProps = (state) => {
    return {
        itemEditing: state.itemEditing
    }
}


const mapDispatchToProps = (dispatch,props) =>{
    return {
        onEditProduct: (id) => {
            dispatch(actGetProductRequest(id));
        }
    }
}


export default connect(mapStateToProps,mapDispatchToProps)(ProductActionPage);
